(function() {
  window['cama_init_posttype_form'] = function() {
    var form;
    form = $("#post_type_form");
    form.find('.unput_upload').input_upload();
    form.find("[name='meta[has_parent_structure]']").change(function() {
      var item;
      item = form.find("#meta_contents_route_format_hierarchy_post");
      item.parent().siblings().find("input").prop("disabled", $(this).is(":checked"));
      if ($(this).is(":checked")) {
        return item.prop("checked", true).prop("disabled", false);
      } else {
        return item.prop("disabled", true);
      }
    }).trigger("change");
    return form.find('[name="meta[has_picture]"]').change(function() {
      var items;
      items = form.find('.picture_settings input');
      if ($(this).is(":checked")) {
        return items.prop("disabled", false);
      } else {
        return items.prop("disabled", true);
      }
    }).trigger("change");
  };

}).call(this);
function init_profile_form(){
    var form = $("#user_form");
    form.validate();

    $('#profie-form-ajax-password').validate({ // change password
        submitHandler: function () {
            showLoading();
            var form2 = $(this.currentForm);
            $.post(form2.attr("action"), form2.serialize(), function (res) {
                form2.flash_message(res);
            }).complete(function () {
                hideLoading();
            });
            return false;
        }
    });

    form.find('.btn_change_photo').click(function(){
        $.fn.upload_filemanager({
            formats: 'image',
            selected: function (file) {
                form.find('#user_meta_avatar').val(file.url);
                form.find('img.img-thumbnail').attr('src', file.url);
            }
        });
        return false;
    });
}
